#!/bin/bash

my_name="atharva"
passwd="shrestha"

echo "Hello $my_name"

#add new user to the system
adduser $my_name --gecos "First Last,RoomNumber,WorkPhone,HomePhone" --disabled-password
echo "$my_name:$passwd" | chpasswd

#add user to sudo group
usermod -aG sudo $my_name

#enable firewall
ufw allow OpenSSH
ufw allow http
ufw allow https
ufw --force enable

#add ssh access to new user
rsync --archive --chown=$my_name:$my_name ~/.ssh /home/$my_name
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                               
